function padleft(str1) {
 s="";

 switch (str1.toString().length)  {
 case 0:
 s=str1+"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;";
 break;
 case 1:
 s=str1+"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;";
 break;
 case 2:
 s=str1+"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;";
 break;
 case 3:
 s=str1+"&nbsp;&nbsp;&nbsp;&nbsp;";
break;
default:
 s=str1+"&nbsp;&nbsp;&nbsp;";
 }
return s;
}
function midleft(str1) {
 s="";

 switch (str1.length)  {
 case 0:
 s="&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;";
 break;
 case 1:
 s="&nbsp;&nbsp;&nbsp;&nbsp;"+str1+"&nbsp;&nbsp;";
 break;
 case 2:
 s="&nbsp;&nbsp;&nbsp;&nbsp;"+str1+"&nbsp;";
 break;
 case 3:
 s="&nbsp;&nbsp;&nbsp;"+str1+"&nbsp;";
break;
default:
 s="&nbsp;&nbsp;&nbsp;"+str1+"";
 }
return s;
}
